// DOM Elements
const themeToggle = document.getElementById('themeToggle');
const langButtons = document.querySelectorAll('.lang-btn');
const body = document.body;
const mobileMenuBtn = document.getElementById('mobileMenuBtn');
const mobileNav = document.getElementById('mobileNav');
const navLinks = document.querySelectorAll('.nav-link, .mobile-nav-link');
const chatWidget = document.getElementById('chatWidget');
const chatButton = document.getElementById('chatButton');
const chatBubble = document.getElementById('chatBubble');
const chatMessages = document.getElementById('chatMessages');
const chatInput = document.getElementById('chatInput');
const sendButton = document.getElementById('sendButton');
const quickReplies = document.querySelectorAll('.quick-reply');

// Initialize page
document.addEventListener('DOMContentLoaded', function() {
    // Initialize Feather icons
    feather.replace();

    // Set initial theme from localStorage or default to light
    const savedTheme = localStorage.getItem('theme') || 'light';
    body.classList.remove('light-theme', 'dark-theme');
    body.classList.add(`${savedTheme}-theme`);

    // Update logo based on initial theme
    updateLogo();

    // Set initial language from localStorage or default to Indonesian
    const savedLang = localStorage.getItem('language') || 'id';
    switchLanguage(savedLang);

    // Setup event listeners
    setupEventListeners();

    // Setup scroll animations
    setupScrollAnimations();

    // Setup sticky header
    setupStickyHeader();

    // Setup navigation
    setupNavigation();

    // Setup chat widget
    setupChatWidget();

    // Setup payment form
    const paymentForm = document.getElementById('paymentForm');
    if (paymentForm) {
        paymentForm.addEventListener('submit', handlePaymentSubmit);
    }
});

// Setup event listeners
function setupEventListeners() {
    // Theme toggle
    themeToggle.addEventListener('click', toggleTheme);

    // Language toggle
    langButtons.forEach(btn => {
        btn.addEventListener('click', (e) => {
            const lang = e.target.getAttribute('data-lang');
            switchLanguage(lang);
        });
    });

    // Smooth scrolling for CTA button
    const ctaButton = document.querySelector('.cta-button');
    if (ctaButton) {
        ctaButton.addEventListener('click', (e) => {
            e.preventDefault();
            document.getElementById('services').scrollIntoView({
                behavior: 'smooth'
            });
        });
    }

    // Mobile menu toggle
    if (mobileMenuBtn) {
        mobileMenuBtn.addEventListener('click', toggleMobileMenu);
    }

    // Navigation link clicks
    navLinks.forEach(link => {
        link.addEventListener('click', (e) => {
            e.preventDefault();
            const targetId = link.getAttribute('href');
            const targetElement = document.querySelector(targetId);

            if (targetElement) {
                // Close mobile menu if open
                if (mobileNav.classList.contains('open')) {
                    toggleMobileMenu();
                }

                // Smooth scroll to target
                const offsetTop = targetElement.offsetTop - 80;
                window.scrollTo({
                    top: offsetTop,
                    behavior: 'smooth'
                });
            }
        });
    });
}

// Theme toggle function
function toggleTheme() {
    const isLight = body.classList.contains('light-theme');

    body.classList.remove('light-theme', 'dark-theme');
    body.classList.add(isLight ? 'dark-theme' : 'light-theme');

    // Update logo based on theme
    updateLogo();

    // Save to localStorage
    localStorage.setItem('theme', isLight ? 'dark' : 'light');

    // Re-initialize Feather icons after theme change
    feather.replace();
}

// Update logo based on current theme
function updateLogo() {
    const logoImg = document.querySelector('.logo-img');
    if (logoImg) {
        const isDark = body.classList.contains('dark-theme');
        logoImg.src = isDark ? 'assets/logo-dark.png' : 'assets/logo.png';
    }
}

// Language switching function
function switchLanguage(lang) {
    currentLang = lang;

    // Update active button
    langButtons.forEach(btn => {
        btn.classList.remove('active');
        if (btn.getAttribute('data-lang') === lang) {
            btn.classList.add('active');
        }
    });

    // Update all text elements with data-key attribute
    const elements = document.querySelectorAll('[data-key]');
    elements.forEach(element => {
        const key = element.getAttribute('data-key');
        if (translations[lang] && translations[lang][key]) {
            element.textContent = translations[lang][key];
        }
    });

    // Update document language attribute
    document.documentElement.lang = lang;

    // Update meta description
    const metaDesc = document.querySelector('meta[name="description"]');
    if (metaDesc) {
        if (lang === 'en') {
            metaDesc.content = 'Boost your Shopee sales effortlessly with AI strategies, store optimization, and comprehensive e-commerce solutions tailored for Indonesian market.';
        } else {
            metaDesc.content = 'Tingkatkan penjualan Shopee Anda dengan mudah. Layanan optimasi toko online, strategi AI, dan solusi yang disesuaikan untuk kesuksesan e-commerce Anda.';
        }
    }

    // Save to localStorage
    localStorage.setItem('language', lang);
}

// Scroll animations setup
function setupScrollAnimations() {
    const observerOptions = {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('visible');
                entry.target.style.animationDelay = `${Math.random() * 0.3}s`;
            }
        });
    }, observerOptions);

    // Observe all animation target elements
    const animationTargets = document.querySelectorAll('.feature-card, .service-card, .client-logo');
    animationTargets.forEach(target => {
        observer.observe(target);
    });
}

// Sticky header setup
function setupStickyHeader() {
    const header = document.getElementById('header');
    let lastScrollY = window.scrollY;

    window.addEventListener('scroll', () => {
        const currentScrollY = window.scrollY;

        if (currentScrollY > 100) {
            header.classList.add('scrolled');

            // Hide/show header based on scroll direction
            if (currentScrollY > lastScrollY && currentScrollY > 200) {
                header.style.transform = 'translateY(-100%)';
            } else {
                header.style.transform = 'translateY(0)';
            }
        } else {
            header.classList.remove('scrolled');
            header.style.transform = 'translateY(0)';
        }

        lastScrollY = currentScrollY;
    });
}

// Smooth scroll polyfill for older browsers
function smoothScrollTo(target, duration = 800) {
    const targetElement = document.querySelector(target);
    if (!targetElement) return;

    const targetPosition = targetElement.offsetTop - 80; // Account for header height
    const startPosition = window.pageYOffset;
    const distance = targetPosition - startPosition;
    let startTime = null;

    function animation(currentTime) {
        if (startTime === null) startTime = currentTime;
        const timeElapsed = currentTime - startTime;
        const run = ease(timeElapsed, startPosition, distance, duration);
        window.scrollTo(0, run);
        if (timeElapsed < duration) requestAnimationFrame(animation);
    }

    function ease(t, b, c, d) {
        t /= d / 2;
        if (t < 1) return c / 2 * t * t + b;
        t--;
        return -c / 2 * (t * (t - 2) - 1) + b;
    }

    requestAnimationFrame(animation);
}

// Performance optimization: Debounce scroll events
function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
        const later = () => {
            clearTimeout(timeout);
            func(...args);
        };
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
    };
}

// Add loading states for better UX
window.addEventListener('load', () => {
    document.body.classList.add('loaded');

    // Trigger initial animations
    setTimeout(() => {
        const heroElements = document.querySelectorAll('.hero-text > *');
        heroElements.forEach((el, index) => {
            setTimeout(() => {
                el.classList.add('fade-in');
            }, index * 100);
        });
    }, 300);
});

// Mobile menu toggle function
function toggleMobileMenu() {
    if (mobileNav) {
        mobileNav.classList.toggle('open');

        // Update mobile menu button icon
        const icon = mobileMenuBtn.querySelector('i');
        if (mobileNav.classList.contains('open')) {
            icon.setAttribute('data-feather', 'x');
        } else {
            icon.setAttribute('data-feather', 'menu');
        }
        feather.replace();
    }
}

// Navigation setup function
function setupNavigation() {
    // Track active section on scroll
    const sections = document.querySelectorAll('section, main');
    const navLinksDesktop = document.querySelectorAll('.nav-link');

    const observerOptions = {
        threshold: 0.3,
        rootMargin: '-80px 0px -50% 0px'
    };

    const navObserver = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const sectionId = entry.target.id;

                // Update active nav link
                navLinksDesktop.forEach(link => {
                    link.classList.remove('active');
                    if (link.getAttribute('href') === `#${sectionId}`) {
                        link.classList.add('active');
                    }
                });
            }
        });
    }, observerOptions);

    sections.forEach(section => {
        if (section.id) {
            navObserver.observe(section);
        }
    });

    // Close mobile menu when clicking outside
    document.addEventListener('click', (e) => {
        if (mobileNav && mobileNav.classList.contains('open')) {
            if (!mobileNav.contains(e.target) && !mobileMenuBtn.contains(e.target)) {
                toggleMobileMenu();
            }
        }
    });

    // Close mobile menu on window resize
    window.addEventListener('resize', () => {
        if (window.innerWidth > 768 && mobileNav && mobileNav.classList.contains('open')) {
            toggleMobileMenu();
        }
    });
}

// Setup chat widget functionality
function setupChatWidget() {
    if (!chatButton || !chatWidget) return;

    // Chat toggle
    chatButton.addEventListener('click', toggleChat);

    // Send message functionality
    if (sendButton && chatInput) {
        sendButton.addEventListener('click', sendMessage);
        chatInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter') {
                sendMessage();
            }
        });
    }

    // Quick reply buttons
    quickReplies.forEach(button => {
        button.addEventListener('click', (e) => {
            const message = e.target.textContent;
            sendUserMessage(message);

            // Generate bot response based on button content
            setTimeout(() => {
                let response;
                if (message.includes('layanan') || message.includes('services')) {
                    response = chatResponses[currentLang].services;
                } else if (message.includes('harga') || message.includes('pricing')) {
                    response = chatResponses[currentLang].pricing;
                } else if (message.includes('hubungi') || message.includes('contact')) {
                    response = chatResponses[currentLang].contact;
                } else {
                    response = chatResponses[currentLang].default;
                }
                sendBotMessage(response);
            }, 800);

            // Hide quick replies after use
            e.target.parentElement.style.display = 'none';
        });
    });

    // Close chat when clicking outside
    document.addEventListener('click', (e) => {
        if (chatWidget.classList.contains('open') && 
            !chatWidget.contains(e.target)) {
            toggleChat();
        }
    });
}

// Toggle chat widget
function toggleChat() {
    chatWidget.classList.toggle('open');

    // Re-initialize Feather icons after toggle
    setTimeout(() => feather.replace(), 100);
}

// Send user message
function sendUserMessage(message) {
    if (!message.trim()) return;

    const messageElement = createMessageElement(message, 'user');
    chatMessages.appendChild(messageElement);
    scrollToBottom();

    if (chatInput) {
        chatInput.value = '';
    }
}

// Send bot message
function sendBotMessage(message) {
    const messageElement = createMessageElement(message, 'bot');
    chatMessages.appendChild(messageElement);
    scrollToBottom();
}

// Create message element
function createMessageElement(text, sender) {
    const messageDiv = document.createElement('div');
    messageDiv.className = `message ${sender}-message`;

    const avatar = document.createElement('div');
    avatar.className = 'message-avatar';
    avatar.innerHTML = sender === 'bot' ? '<i data-feather="cpu"></i>' : '<i data-feather="user"></i>';

    const content = document.createElement('div');
    content.className = 'message-content';

    const textP = document.createElement('p');
    textP.textContent = text;

    const time = document.createElement('span');
    time.className = 'message-time';
    time.textContent = new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});

    content.appendChild(textP);
    content.appendChild(time);
    messageDiv.appendChild(avatar);
    messageDiv.appendChild(content);

    // Re-initialize feather icons for new message
    setTimeout(() => feather.replace(), 50);

    return messageDiv;
}

// Send message function
function sendMessage() {
    if (!chatInput) return;

    const message = chatInput.value.trim();
    if (!message) return;

    sendUserMessage(message);

    // Simulate bot response
    setTimeout(() => {
        const response = generateBotResponse(message.toLowerCase());
        sendBotMessage(response);
    }, 1000);
}

// Generate bot response based on keywords
function generateBotResponse(message) {
    if (message.includes('layanan') || message.includes('service')) {
        return chatResponses[currentLang].services;
    } else if (message.includes('harga') || message.includes('price') || message.includes('biaya')) {
        return chatResponses[currentLang].pricing;
    } else if (message.includes('kontak') || message.includes('contact') || message.includes('hubungi')) {
        return chatResponses[currentLang].contact;
    } else if (message.includes('halo') || message.includes('hello') || message.includes('hai')) {
        return currentLang === 'id' ? 
            "Halo! Senang bertemu dengan Anda. Ada yang bisa saya bantu tentang layanan Sellaro hari ini? ðŸ˜Š" :
            "Hello! Nice to meet you. Is there anything I can help you with about Sellaro services today? ðŸ˜Š";
    } else if (message.includes('terima kasih') || message.includes('thank')) {
        return currentLang === 'id' ? 
            "Sama-sama! Jika ada pertanyaan lain, jangan ragu untuk bertanya. Tim kami siap membantu! ðŸ™Œ" :
            "You're welcome! If you have any other questions, don't hesitate to ask. Our team is ready to help! ðŸ™Œ";
    } else {
        return chatResponses[currentLang].default;
    }
}

// Scroll chat to bottom
function scrollToBottom() {
    if (chatMessages) {
        chatMessages.scrollTop = chatMessages.scrollHeight;
    }
}

// Scroll to packages section
function scrollToPackages() {
    document.getElementById('packages').scrollIntoView({
        behavior: 'smooth',
        block: 'start'
    });
}

// Payment modal functions
function openPaymentModal() {
    const modal = document.getElementById('paymentModal');
    modal.classList.add('show');
    document.body.style.overflow = 'hidden';

    // Re-initialize Feather icons
    setTimeout(() => feather.replace(), 100);
}

function closePaymentModal() {
    const modal = document.getElementById('paymentModal');
    modal.classList.remove('show');
    document.body.style.overflow = 'auto';
}

// Handle payment form submission
function handlePaymentSubmit(event) {
    event.preventDefault();

    const form = event.target;
    const formData = new FormData(form);

    // Simple validation
    const name = formData.get('customerName').trim();
    const email = formData.get('customerEmail').trim();
    const phone = formData.get('customerPhone').trim();

    if (name && email && phone) {
        // Store customer info in localStorage for the success page
        localStorage.setItem('customerEmail', email);

        // Redirect to success page
        window.location.href = '/successful-payment';
    } else {
        alert('Please fill in all fields.');
    }
}

styles/main.css.content:
/* CSS Variables for Theme Management */
:root {
    /* Colors */
    --primary-color: #0480FE;
    --secondary-color: #0BC4EE;
    --dark-bg: #0B0F1A;
    --light-bg: #F7F9FC;
    --dark-text: #FFFFFF;
    --light-text: #1A1A1A;

    /* Current theme colors */
    --bg-color: var(--light-bg);
    --text-color: var(--light-text);
    --card-bg: #FFFFFF;
    --border-color: #E5E7EB;
    --shadow: rgba(0, 0, 0, 0.1);

    /* Typography */
    --font-heading: 'Space Grotesk', sans-serif;
    --font-body: 'Manrope', sans-serif;

    /* Spacing */
    --section-padding: 80px 0;
    --container-padding: 0 20px;

    /* Transitions */
    --transition: all 0.3s ease;
    --theme-transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
}

/* Dark Theme Variables */
.dark-theme {
    --bg-color: var(--dark-bg);
    --text-color: var(--dark-text);
    --card-bg: #1A1F2E;
    --border-color: #2D3748;
    --shadow: rgba(0, 0, 0, 0.3);
}

/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: var(--font-body);
    background-color: var(--bg-color);
    color: var(--text-color);
    line-height: 1.6;
    transition: var(--theme-transition);
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: var(--container-padding);
}

/* Header Styles */
.header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background-color: var(--bg-color);
    border-bottom: 1px solid var(--border-color);
    backdrop-filter: blur(10px);
    z-index: 1000;
    transition: var(--theme-transition);
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 15px 0;
    gap: 40px;
}

.logo-img {
    height: 40px;
    width: auto;
    transition: var(--transition);
}

/* Logo adjustments for different themes */
.logo-img {
    transition: var(--transition);
}

/* Navigation Styles */
.nav {
    display: flex;
    align-items: center;
    gap: 30px;
    flex: 1;
    justify-content: center;
}

.nav-link {
    text-decoration: none;
    color: var(--text-color);
    font-family: var(--font-body);
    font-weight: 500;
    font-size: 0.95rem;
    padding: 8px 16px;
    border-radius: 20px;
    transition: var(--transition);
    position: relative;
}

.nav-link:hover {
    color: var(--primary-color);
    background-color: var(--border-color);
}

.nav-link.active {
    color: var(--primary-color);
    background-color: var(--border-color);
}

/* Mobile Navigation */
.mobile-nav {
    display: none;
    flex-direction: column;
    background-color: var(--card-bg);
    border-top: 1px solid var(--border-color);
    padding: 20px 0;
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    transform: translateY(-100%);
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
}

.mobile-nav.open {
    transform: translateY(0);
    opacity: 1;
    visibility: visible;
}

.mobile-nav-link {
    text-decoration: none;
    color: var(--text-color);
    font-family: var(--font-body);
    font-weight: 500;
    padding: 15px 20px;
    border-bottom: 1px solid var(--border-color);
    transition: var(--transition);
}

.mobile-nav-link:hover {
    color: var(--primary-color);
    background-color: var(--border-color);
}

.mobile-nav-link:last-child {
    border-bottom: none;
}

/* Mobile Menu Button */
.mobile-menu-btn {
    display: none;
    background: none;
    border: none;
    color: var(--text-color);
    cursor: pointer;
    padding: 8px;
    border-radius: 8px;
    transition: var(--transition);
}

.mobile-menu-btn:hover {
    background-color: var(--border-color);
}

.mobile-menu-btn i {
    width: 24px;
    height: 24px;
}

.header-controls {
    display: flex;
    align-items: center;
    gap: 20px;
}

/* Language Toggle */
.language-toggle {
    display: flex;
    background-color: var(--card-bg);
    border-radius: 25px;
    padding: 4px;
    border: 1px solid var(--border-color);
    transition: var(--theme-transition);
}

.lang-btn {
    background: none;
    border: none;
    padding: 8px 16px;
    border-radius: 20px;
    font-family: var(--font-body);
    font-weight: 500;
    color: var(--text-color);
    cursor: pointer;
    transition: var(--transition);
}

.lang-btn.active {
    background-color: var(--primary-color);
    color: white;
}

.lang-btn:hover:not(.active) {
    background-color: var(--border-color);
}

/* Theme Toggle */
.theme-toggle {
    background: var(--card-bg);
    border: 1px solid var(--border-color);
    border-radius: 50%;
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: var(--theme-transition);
    position: relative;
}

.theme-icon {
    width: 20px;
    height: 20px;
    color: var(--text-color);
    transition: var(--transition);
    position: absolute;
}

.light-theme .dark-icon {
    opacity: 0;
    transform: rotate(180deg);
}

.dark-theme .light-icon {
    opacity: 0;
    transform: rotate(-180deg);
}

.theme-toggle:hover {
    transform: scale(1.05);
    border-color: var(--primary-color);
}

/* Hero Section */
.hero {
    padding: 120px 0 var(--section-padding);
    min-height: 100vh;
    display: flex;
    align-items: center;
}

.hero-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
}

.hero-title {
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 3.5rem;
    line-height: 1.1;
    margin-bottom: 20px;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.hero-subtitle {
    font-size: 1.5rem;
    color: var(--secondary-color);
    font-weight: 600;
    margin-bottom: 20px;
}

.hero-description {
    font-size: 1.1rem;
    margin-bottom: 30px;
    opacity: 0.8;
}

.cta-button {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    color: white;
    border: none;
    padding: 15px 30px;
    border-radius: 30px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition);
    font-family: var(--font-body);
}

.cta-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(4, 128, 254, 0.3);
}

.hero-img {
    width: 100%;
    height: auto;
    border-radius: 20px;
}

/* Section Titles */
.section-title {
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 2.5rem;
    text-align: center;
    margin-bottom: 60px;
    color: var(--text-color);
}

/* Clients Section */
.clients {
    padding: var(--section-padding);
    background-color: var(--card-bg);
    transition: var(--theme-transition);
}

.clients-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 40px;
    margin-bottom: 30px;
    align-items: center;
}

.client-logo {
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 20px;
    background-color: var(--bg-color);
    border-radius: 15px;
    border: 1px solid var(--border-color);
    transition: var(--theme-transition);
    height: 120px;
}

.client-logo img {
    max-width: 120px;
    max-height: 80px;
    width: auto;
    height: auto;
    transition: var(--transition);
}

.client-logo:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px var(--shadow);
}

/* Dark theme specific styling for client logos */
.dark-theme .client-logo {
    background-color: rgba(255, 255, 255, 0.95);
    border: 1px solid rgba(255, 255, 255, 0.2);
}

.dark-theme .client-logo:hover {
    background-color: rgba(255, 255, 255, 1);
    transform: translateY(-5px);
}

.clients-more {
    text-align: center;
    font-size: 1.1rem;
    opacity: 0.7;
}

/* Why Choose Us Section */
.why-choose {
    padding: var(--section-padding);
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 30px;
}

.feature-card {
    background-color: var(--card-bg);
    padding: 40px 30px;
    border-radius: 20px;
    text-align: center;
    border: 1px solid var(--border-color);
    transition: var(--theme-transition);
    position: relative;
    overflow: hidden;
}

.feature-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 3px;
    background: linear-gradient(90deg, var(--primary-color), var(--secondary-color));
    transition: var(--transition);
}

.feature-card:hover::before {
    left: 0;
}

.feature-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px var(--shadow);
}

.feature-icon {
    width: 80px;
    height: 80px;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 20px;
    color: white;
}

.feature-icon i {
    width: 32px;
    height: 32px;
}

.feature-title {
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 1.3rem;
    margin-bottom: 15px;
    color: var(--text-color);
}

.feature-description {
    opacity: 0.8;
    line-height: 1.6;
}

/* Services Section */
.services {
    padding: var(--section-padding);
    background-color: var(--card-bg);
    transition: var(--theme-transition);
}

.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 30px;
}

.service-card {
    background-color: var(--bg-color);
    padding: 40px 30px;
    border-radius: 20px;
    border: 1px solid var(--border-color);
    transition: var(--theme-transition);
    position: relative;
    overflow: hidden;
}

.service-card::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: conic-gradient(from 0deg, transparent, var(--primary-color), transparent);
    opacity: 0;
    transition: var(--transition);
    animation: rotate 3s linear infinite;
}

.service-card:hover::before {
    opacity: 0.1;
}

.service-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px var(--shadow);
}

@keyframes rotate {
    to {
        transform: rotate(360deg);
    }
}

.service-icon {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    border-radius: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 20px;
    color: white;
}

.service-icon i {
    width: 28px;
    height: 28px;
}

.service-title {
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 1.3rem;
    margin-bottom: 15px;
    color: var(--text-color);
}

.service-description {
    opacity: 0.8;
    line-height: 1.6;
}

/* Footer */
.footer {
    padding: 40px 0;
    background-color: var(--dark-bg);
    color: var(--dark-text);
    text-align: center;
}

.footer-brand h3 {
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 1.5rem;
    margin-bottom: 10px;
}

.footer-brand p {
    opacity: 0.8;
}

/* Packages Section */
.packages {
    padding: var(--section-padding);
    background: linear-gradient(135deg, var(--bg-color) 0%, var(--card-bg) 100%);
    transition: var(--theme-transition);
}

.package-container {
    display: flex;
    justify-content: center;
    align-items: center;
}

.package-card {
    background: var(--card-bg);
    border-radius: 25px;
    padding: 40px 35px;
    border: 2px solid var(--border-color);
    transition: var(--theme-transition);
    max-width: 500px;
    width: 100%;
    position: relative;
    overflow: hidden;
}

.package-card.featured {
    border: 2px solid var(--primary-color);
    transform: scale(1.05);
    box-shadow: 0 20px 40px rgba(4, 128, 254, 0.2);
}

.package-card.featured::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, var(--primary-color), var(--secondary-color));
}

.package-header {
    text-align: center;
    margin-bottom: 35px;
}

.package-title {
    font-family: var(--font-heading);
    font-size: 1.8rem;
    font-weight: 700;
    color: var(--text-color);
    margin-bottom: 15px;
}

.package-price {
    display: flex;
    align-items: baseline;
    justify-content: center;
    gap: 5px;
    margin-bottom: 10px;
}

.price-currency {
    font-size: 1.2rem;
    color: var(--text-secondary);
    font-weight: 600;
}

.price-amount {
    font-size: 3rem;
    font-weight: 800;
    color: var(--primary-color);
    font-family: var(--font-heading);
}

.price-period {
    font-size: 1rem;
    color: var(--text-secondary);
    font-weight: 500;
}

.package-features {
    margin-bottom: 35px;
}

.feature-item {
    display: flex;
    align-items: flex-start;
    gap: 15px;
    margin-bottom: 25px;
    padding: 20px;
    background: var(--bg-color);
    border-radius: 15px;
    border: 1px solid var(--border-color);
    transition: var(--theme-transition);
}

.feature-item:hover {
    transform: translateX(5px);
    border-color: var(--primary-color);
}

.feature-item .feature-icon {
    color: var(--primary-color);
    width: 24px;
    height: 24px;
    flex-shrink: 0;
    margin-top: 2px;
}

.feature-content h4 {
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 1.1rem;
    color: var(--text-color);
    margin-bottom: 8px;
}

.feature-content p {
    color: var(--text-secondary);
    line-height: 1.5;
    margin: 0;
}

.package-cta {
    width: 100%;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    color: white;
    border: none;
    padding: 18px 35px;
    border-radius: 15px;
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 1.1rem;
    cursor: pointer;
    transition: var(--transition);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.package-cta:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 30px rgba(4, 128, 254, 0.4);
}

.package-cta:active {
    transform: translateY(-1px);
}

/* Responsive Design for Packages */
@media (max-width: 768px) {
    .package-card.featured {
        transform: none;
        margin: 0 20px;
    }

    .package-card {
        padding: 30px 25px;
    }

    .price-amount {
        font-size: 2.5rem;
    }

    .feature-item {
        padding: 15px;
        gap: 12px;
    }
}

/* Login Button Styles */
.login-btn {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color)) !important;
    color: white !important;
    padding: 10px 20px !important;
    border-radius: 10px !important;
    font-weight: 600 !important;
    transition: var(--transition) !important;
}

.login-btn:hover {
    transform: translateY(-2px) !important;
    box-shadow: 0 10px 20px rgba(4, 128, 254, 0.3) !important;
}

/* Payment Modal Styles */
.payment-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 10000;
    display: none;
    align-items: center;
    justify-content: center;
}

.payment-modal.show {
    display: flex;
}

.modal-backdrop {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.7);
    backdrop-filter: blur(5px);
}

.modal-content {
    position: relative;
    background: var(--card-bg);
    border-radius: 25px;
    max-width: 500px;
    width: 90%;
    max-height: 90vh;
    overflow-y: auto;
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.3);
    border: 2px solid var(--primary-color);
    animation: modalSlideIn 0.3s ease-out;
}

@keyframes modalSlideIn {
    from {
        opacity: 0;
        transform: translateY(-50px) scale(0.9);
    }
    to {
        opacity: 1;
        transform: translateY(0) scale(1);
    }
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 25px 30px;
    border-bottom: 1px solid var(--border-color);
}

.modal-header h3 {
    font-family: var(--font-heading);
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--text-color);
    margin: 0;
}

.modal-close {
    background: none;
    border: none;
    color: var(--text-secondary);
    cursor: pointer;
    padding: 5px;
    border-radius: 50%;
    transition: var(--transition);
}

.modal-close:hover {
    background: var(--border-color);
    color: var(--text-color);
}

.modal-body {
    padding: 30px;
}

.package-summary {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    color: white;
    padding: 20px;
    border-radius: 15px;
    text-align: center;
    margin-bottom: 25px;
}

.package-summary h4 {
    font-family: var(--font-heading);
    font-size: 1.3rem;
    font-weight: 700;
    margin-bottom: 10px;
}

.price-display {
    display: flex;
    align-items: baseline;
    justify-content: center;
    gap: 5px;
}

.price-display .price {
    font-size: 2rem;
    font-weight: 800;
    font-family: var(--font-heading);
}

.price-display .period {
    font-size: 1rem;
    opacity: 0.9;
}

.payment-form {
    display: flex;
    flex-direction: column;
    gap: 20px;
}

.form-group {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.form-group label {
    font-weight: 600;
    color: var(--text-color);
    font-size: 0.95rem;
}

.form-group input {
    padding: 15px;
    border: 1px solid var(--border-color);
    border-radius: 12px;
    background: var(--bg-color);
    color: var(--text-color);
    font-size: 1rem;
    transition: var(--transition);
}

.form-group input:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(4, 128, 254, 0.1);
}

.payment-btn {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    color: white;
    border: none;
    padding: 18px 30px;
    border-radius: 15px;
    font-family: var(--font-heading);
    font-weight: 700;
    font-size: 1.1rem;
    cursor: pointer;
    transition: var(--transition);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    margin-top: 10px;
}

.payment-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 30px rgba(4, 128, 254, 0.4);
}

.payment-btn:active {
    transform: translateY(-1px);
}

@media (max-width: 768px) {
    .modal-content {
        margin: 20px;
        width: calc(100% - 40px);
    }

    .modal-header, .modal-body {
        padding: 20px;
    }

    .price-display .price {
        font-size: 1.8rem;
    }
}

/* Chat Widget Styles */
.chat-widget {
    position: fixed;
    bottom: 20px;
    right: 20px;
    z-index: 1001;
}

.chat-button {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    border: none;
    color: white;
    cursor: pointer;
    box-shadow: 0 4px 20px rgba(4, 128, 254, 0.3);
    transition: var(--transition);
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
}

.chat-button:hover {
    transform: scale(1.1);
    box-shadow: 0 6px 25px rgba(4, 128, 254, 0.4);
}

.chat-icon, .close-icon {
    width: 24px;
    height: 24px;
    position: absolute;
    transition: var(--transition);
}

.close-icon {
    opacity: 0;
    transform: rotate(180deg);
}

.chat-widget.open .chat-icon {
    opacity: 0;
    transform: rotate(-180deg);
}

.chat-widget.open .close-icon {
    opacity: 1;
    transform: rotate(0deg);
}

.chat-bubble {
    position: absolute;
    bottom: 75px;
    right: 0;
    width: 350px;
    max-height: 500px;
    background-color: var(--card-bg);
    border-radius: 20px;
    box-shadow: 0 10px 40px var(--shadow);
    border: 1px solid var(--border-color);
    transform: translateY(20px) scale(0.8);
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    overflow: hidden;
}

.chat-widget.open .chat-bubble {
    transform: translateY(0) scale(1);
    opacity: 1;
    visibility: visible;
}

.chat-header {
    padding: 20px;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    color: white;
}

.chat-header h4 {
    margin: 0 0 5px 0;
    font-family: var(--font-heading);
    font-weight: 600;
    font-size: 1.1rem;
}

.chat-header p {
    margin: 0;
    opacity: 0.9;
    font-size: 0.9rem;
}

.chat-messages {
    max-height: 300px;
    overflow-y: auto;
    padding: 20px;
    display: flex;
    flex-direction: column;
    gap: 15px;
}

.message {
    display: flex;
    gap: 10px;
    animation: slideInMessage 0.3s ease;
}

.user-message {
    flex-direction: row-reverse;
}

.message-avatar {
    width: 35px;
    height: 35px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    flex-shrink: 0;
}

.user-message .message-avatar {
    background: var(--border-color);
    color: var(--text-color);
}

.message-avatar i {
    width: 18px;
    height: 18px;
}

.message-content {
    flex: 1;
    min-width: 0;
}

.message-content p {
    background-color: var(--bg-color);
    padding: 12px 15px;
    border-radius: 15px;
    margin: 0 0 5px 0;
    border: 1px solid var(--border-color);
    word-wrap: break-word;
}

.user-message .message-content p {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    color: white;
    border: none;
}

.message-time {
    font-size: 0.75rem;
    color: var(--text-color);
    opacity: 0.6;
    margin-left: 15px;
}

.user-message .message-time {
    margin-left: 0;
    margin-right: 15px;
    text-align: right;
}

.quick-replies {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-top: 10px;
}

.quick-reply {
    background-color: var(--bg-color);
    border: 1px solid var(--border-color);
    border-radius: 20px;
    padding: 10px 15px;
    font-size: 0.9rem;
    cursor: pointer;
    transition: var(--transition);
    text-align: left;
    color: var(--text-color);
}

.quick-reply:hover {
    background-color: var(--primary-color);
    color: white;
    border-color: var(--primary-color);
}

.chat-input {
    display: flex;
    padding: 15px 20px;
    border-top: 1px solid var(--border-color);
    gap: 10px;
}

.chat-input input {
    flex: 1;
    border: 1px solid var(--border-color);
    border-radius: 25px;
    padding: 12px 18px;
    font-size: 0.9rem;
    background-color: var(--bg-color);
    color: var(--text-color);
    outline: none;
    transition: var(--transition);
}

.chat-input input:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(4, 128, 254, 0.1);
}

.chat-input input::placeholder {
    color: var(--text-color);
    opacity: 0.5;
}

#sendButton {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    border: none;
    color: white;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: var(--transition);
}

#sendButton:hover {
    transform: scale(1.05);
}

#sendButton i {
    width: 18px;
    height: 18px;
}

@keyframes slideInMessage {
    from {
        opacity: 0;
        transform: translateY(10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Mobile responsive for chat */
@media (max-width: 480px) {
    .chat-bubble {
        width: calc(100vw - 40px);
        right: -15px;
    }

    .chat-widget {
        right: 15px;
        bottom: 15px;
    }
}

/* Animations */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in {
    animation: fadeInUp 0.6s ease forwards;
}

/* Responsive Design */
@media (max-width: 768px) {
    .container {
        padding: 0 15px;
    }

    .header-content {
        padding: 10px 0;
        gap: 20px;
    }

    .header-controls {
        gap: 15px;
    }

    /* Hide desktop nav and show mobile menu button */
    .nav {
        display: none;
    }

    .mobile-menu-btn {
        display: block;
    }

    .mobile-nav {
        display: flex;
    }

    .hero {
        padding: 100px 0 60px;
        min-height: auto;
    }

    .hero-content {
        grid-template-columns: 1fr;
        gap: 40px;
        text-align: center;
    }

    .hero-title {
        font-size: 2.5rem;
    }

    .hero-subtitle {
        font-size: 1.3rem;
    }

    .section-title {
        font-size: 2rem;
        margin-bottom: 40px;
    }

    .features-grid {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .services-grid {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    .clients-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 20px;
    }

    .feature-card,
    .service-card {
        padding: 30px 20px;
    }

    :root {
        --section-padding: 60px 0;
    }
}

@media (max-width: 480px) {
    .hero-title {
        font-size: 2rem;
    }

    .clients-grid {
        grid-template-columns: 1fr;
    }

    .lang-btn {
        padding: 6px 12px;
        font-size: 0.9rem;
    }

    .theme-toggle {
        width: 40px;
        height: 40px;
    }
}

/* Smooth scrolling */
html {
    scroll-behavior: smooth;
}

/* Loading and transition effects */
* {
    transition: var(--theme-transition);
}

/* Hide elements initially for scroll animations */
.feature-card,
.service-card,
.client-logo {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 0.6s ease, transform 0.6s ease;
}

.feature-card.visible,
.service-card.visible,
.client-logo.visible {
    opacity: 1;
    transform: translateY(0);
}
